
doc = activeDocument
app.preferences.rulerUnits = Units.PIXELS
app.preferences.typeunits = TypeUnits.PIXELS
var thePathActions = "//172.16.2.2/Academy/Automation_by_Academy/Actions/New_Layer_Mask";

logAction("Layer_mask");
function main() {
    if (!hasSelection()) {
        length = doc.layers.length
        for (var i = 0; i < length; i++) {
            doc.activeLayer = doc.artLayers[i]
            if (i != length - 1) {
                doc.activeLayer.visible = false
                if (hasLayerMask()) {
                    loadSelectionMask()
                    deleteMask()
                }
            } else {
                doc.guides.removeAll()
                doc.activeLayer.visible = true
                doc.activeLayer = doc.artLayers[i]
                selectLayer(doc.artLayers[i].name)
                if (hasSelection()) {
                    if (hasLayerMask()) {
                        deleteMask()
                    }
                } else {
                    if (hasLayerMask()) {
                        loadSelectionMask()
                        deleteMask()
                    } else {
                        autoCutout(true)
                        try { doc.pathItems.getByName("Work Path").deselect() } catch (error) { }
                        loadAction("Fix Selection no selection", "New Layer Mask.atn")
                        break
                    }
                }
                try { doc.pathItems.getByName("Work Path").deselect() } catch (error) { }
                loadAction("Fix Selection", "New Layer Mask.atn")
            }
        }
    } else {
        length = doc.layers.length
        doc.activeLayer = doc.artLayers[length - 1]
        //add mask
        try { doc.pathItems.getByName("Work Path").deselect() } catch (error) { }
        doc.selection.feather(autoFeather())
        addMask()
        try { makeSlice() } catch (error) { }
        loadAction("Add Mask", "New Layer Mask.atn")
    }
}

function autoFeather() {
    var ww = doc.width.as('px')
    var hh = doc.height.as('px')
    var thisSize = Math.min(ww, hh)

    if (thisSize < 500) return 0.1 + 0.2
    if (thisSize < 1000) return 0.2 + 0.2
    if (thisSize < 1500) return 0.3 + 0.2
    if (thisSize < 2000) return 0.4 + 0.2
    if (thisSize < 2500) return 0.5 + 0.2
    if (thisSize < 3000) return 0.6 + 0.2
    if (thisSize < 3500) return 0.7 + 0.2
    if (thisSize < 4000) return 0.8 + 0.2
    if (thisSize < 4500) return 0.9 + 0.2
    if (thisSize < 5000) return 1.0 + 0.2
    if (thisSize < 5500) return 1.1 + 0.2
    if (thisSize < 6000) return 1.2 + 0.2
    if (thisSize < 6500) return 1.3 + 0.2
    if (thisSize < 7000) return 1.4 + 0.2
    if (thisSize < 7500) return 1.5 + 0.2
    if (thisSize < 8000) return 1.6 + 0.2
    if (thisSize < 8500) return 1.7 + 0.2
    if (thisSize < 9000) return 1.8 + 0.2
    if (thisSize < 9500) return 1.9 + 0.2
    if (thisSize < 10000) return 2.0 + 0.2
    if (thisSize < 10500) return 2.1 + 0.2
    if (thisSize >= 10500) return 2.2 + 0.2
    ///////////////////////
}
 

function hasSelection() {
    var hasSelection = false;
    var ref = new ActionReference();
    ref.putProperty(stringIDToTypeID("property"), stringIDToTypeID("selection"));
    ref.putEnumerated(stringIDToTypeID("document"), stringIDToTypeID("ordinal"), stringIDToTypeID("targetEnum"));
    var desc = executeActionGet(ref);
    if (desc.count) hasSelection = true;
    return hasSelection;
}

//kiem tra layer co ton tai mask khong
function hasLayerMask() {
    var reference = new ActionReference();
    reference.putEnumerated(stringIDToTypeID("layer"), stringIDToTypeID("ordinal"), stringIDToTypeID("targetEnum"));
    var desc = executeActionGet(reference);
    return desc.getBoolean(stringIDToTypeID("hasUserMask"));
}
function deleteMask() {
    var idDlt = charIDToTypeID("Dlt ");
    var desc26 = new ActionDescriptor();
    var idnull = charIDToTypeID("null");
    var ref22 = new ActionReference();
    var idChnl = charIDToTypeID("Chnl");
    var idChnl = charIDToTypeID("Chnl");
    var idMsk = charIDToTypeID("Msk ");
    ref22.putEnumerated(idChnl, idChnl, idMsk);
    desc26.putReference(idnull, ref22);
    executeAction(idDlt, desc26, DialogModes.NO);
}

