function makeSlice() {
    var c2t = function (s) {
        return app.charIDToTypeID(s);
    };

    var s2t = function (s) {
        return app.stringIDToTypeID(s);
    };

    var descriptor = new ActionDescriptor();
    var descriptor2 = new ActionDescriptor();
    var reference = new ActionReference();
    var reference2 = new ActionReference();

    reference.putClass(s2t("slice"));
    descriptor.putReference(c2t("null"), reference);
    descriptor2.putEnumerated(s2t("type"), s2t("sliceType"), s2t("layer"));
    reference2.putEnumerated(s2t("layer"), s2t("ordinal"), s2t("targetEnum"));
    descriptor2.putReference(s2t("layer"), reference2);
    descriptor.putObject(s2t("using"), s2t("slice"), descriptor2);
    executeAction(s2t("make"), descriptor, DialogModes.NO);
}
//select layer
function selectLayer(nameLayer) {
    var result = false;
    try {
        var c2t = function (s) {
            return app.charIDToTypeID(s);
        };

        var s2t = function (s) {
            return app.stringIDToTypeID(s);
        };

        var descriptor = new ActionDescriptor();
        var reference = new ActionReference();

        reference.putName(s2t("layer"), nameLayer);
        descriptor.putReference(c2t("null"), reference);
        executeAction(s2t("select"), descriptor, DialogModes.NO);
        result = true;
    } catch (error) {
        result = false;
    }
    return result;
}

